def compute_headers(stream):
  headers = {}
  for {key, value} in connection_headers[C]:
    headers[key] = value
  for {key, value} in stream_group_headers[G]:
    headers[key] = value
  for {key, value} in headers_frame:
    headers[key] = value
  return headers

